if (msg.topic == "state"){
    // Define the mapping of hex values to status strings
    const statusMapping = {
        0x00: "Standby",
        0x01: "Fault",
        0x02: "Programming",
        0x04: "PV on-grid mode",
        0x08: "PV Charge mode",
        0x0C: "PV Charge + on-grid mode",
        0x10: "Battery on-grid mode",
        0x14: "PV + Battery on-grid mode",
        0x20: "AC Charge Mode",
        0x28: "PV + AC charge mode",
        0x40: "Battery off-grid mode",
        0x80: "PV off-grid mode",
        0xC0: "PV + battery off-grid mode",
        0x88: "PV charge + off-grid mode"
    };

    // Check if the payload exists in the mapping
    if (statusMapping.hasOwnProperty(msg.payload)) {
        msg.payload = statusMapping[msg.payload]; // Convert the payload
    } else {
        msg.payload = "Unknown Status"; // Default value for unmapped payloads
    }

}
